class Solution(object):
    def __init__(self):
        self.pairs = None

    def movable(self, f, t):
        return self.pairs[f][1] < self.pairs[t][0]


    def findLongestChain(self, pairs):
        t = [0] * len(pairs)
        h = {}

        pairs = sorted(pairs, key=lambda x: x[1])
        self.pairs = pairs
        # print pairs

        t[0] = 1
        for i in range(1, len(t)):
            max_v = 0
            for j in range(0, i):
                if self.pairs[i][0] <= self.pairs[j][1]:
                    break
                if t[j] > max_v:
                    max_v = t[j]
            t[i] = max_v + 1
            # print t
        return max(t)



